import React,{useState} from 'react'

import "../Styles/TxtArea.css"
export default function Apuntes() {
    const [option, setoption] = useState(1)
    const opt =()=>{
        if(option==1){
            return(
                <>
                <div class="four column centered row">
                    <form class="ui form"><textarea placeholder="Ingrese su apunte" className="tamanio"  ></textarea></form>
                </div>
                </>
            )
        }else{
            return(
                <>
                    <div>
                        ver apuntes
                    </div>
                </>
            )
        }
    }
    return (
        <div className="ui column centered ">
            <div class="column">
            <div class="ui buttons">
                <button class="ui button" onChange={setoption(1)} onClick={opt}>Nuevo apunte</button>
                <div class="or"></div>
                <button class="ui positive button" onChange={setoption(2)} onClick={opt}>Ver apuntes</button>
                </div>
            </div>

        </div>
    )
}
